# -*- coding: utf-8 -*-# Robot server interface# @author william# @version 1.0from scrapy import cmdlineclass RobotServer(object):    """ Robot server """    robots = [        "news",        "page",        "wechat",        "weibo",    ]    def start_robot(self, robot_name):        cmdline.execute(("scrapy crawl %s" % robot_name).split())    def start_all_robot(self):        for r in self.robots:            cmdline.execute(("scrapy crawl %s" % r).split())